import { uiActions } from "./ui-slice";
import { cartActions } from "./cart-slice";


export const putRequest=(cart)=>{
    return async(dispatch)=>{
            dispatch(uiActions.setNotification({
                status:"pending",
                title:"Pending...",
                message:"Sending cart data!"
            }));
            async function httpRequest(){
                const response= await fetch("https://food-app-a6f82-default-rtdb.firebaseio.com/cart.json",{
                    method: "PUT",
                    body: JSON.stringify({
                        cartItems:cart.cartItems,
                        totalQuantity:cart.totalQuantity
                    }),
                });
        
                if(!response.ok){
                    throw new Error("Error");
                }
            }

        try{
            await httpRequest();
            dispatch(uiActions.setNotification({
                status:"success",
                title:"Success!",
                message:"Successfully sent cart data!",
            }));
        }
        catch(err){
            dispatch(uiActions.setNotification({
                status:"error",
                title:"Error!",
                message:"Failed to send cart data!",
            }));
        }
    }
}

export const getRequest=()=>{
    return async(dispatch)=>{
            dispatch(uiActions.setNotification({
                status:"pending",
                title:"Pending...",
                message:"Fetching cart data!"
            }));
            async function httpRequest(){
                const response= await fetch("https://food-app-a6f82-default-rtdb.firebaseio.com/cart.json");
        
                if(!response.ok){
                    throw new Error("Error");
                }

                return response.cart;
            }

        try{
            const data=await httpRequest();
            dispatch(cartActions.replaceCart({items:data.cartItems, totalQuantity:data.totalQuantity}));
            dispatch(uiActions.setNotification({
                status:"success",
                title:"Success!",
                message:"Successfully fetched cart data!",
            }));
        }
        catch(err){
            dispatch(uiActions.setNotification({
                status:"error",
                title:"Error!",
                message:"Failed to fetch cart data!",
            }));
        }
    }
}
