import { useEffect } from 'react';
import Cart from './components/Cart/Cart';
import Layout from './components/Layout/Layout';
import Products from './components/Shop/Products';

import { useDispatch, useSelector } from 'react-redux';
import { putRequest } from './store/cart-slice';

let isFirstTime=true;

function App() {
  const dispatch=useDispatch();

  const isShowCart=useSelector(state=>state.uiKey.isShowCart);

  const notification=useSelector(state=>state.uiKey.notification);

  const cart=useSelector(state=>state.cartKey.cartItems);

  useEffect(()=>{
    if(cart.isChanged){
      dispatch(putRequest(cart));
    }
  },[dispatch]);

  return (
    <>
      {notification && <main>
          <h2>{notification.title}</h2>
          <p>{notification.message}</p>
        </main>}
      <Layout>
      {isShowCart && <Cart />}
      <Products />
    </Layout>
    </>
  );
}

export default App;
