import Cart from './components/Cart/Cart';
import Layout from './components/Layout/Layout';
import Products from './components/Shop/Products';

import { useSelector } from 'react-redux';

import { useEffect } from 'react';

import { useDispatch } from 'react-redux';
import { cartToggleActions } from './store/cartShowToggle-slice';

function App() {
  const dispatch=useDispatch();

  const cart=useSelector((state)=>state.cartKey.items);

  const isCartShow=useSelector((state)=>state.cartToggleKey.isCartShow);

  let firstExecution=true;

  useEffect(()=>{
    const sendcartData=async()=>{

      if(firstExecution){
        firstExecution=false;
        return;
      }

      dispatch(cartToggleActions.setNotification({
        status: "pending", 
        title: "Sending",
        message: "Sending cart data...",
      }));

      const response=await fetch("cart.json",{
        method:"PUT",
        headers:{
          "Content-Type":"application/json",
        },
        body:JSON.stringify(cart),
      });

      if(!response.ok){
        throw new Error("Sending cartdata failed!");
      }

      dispatch(cartToggleActions.setNotification({
        status: "success", 
        title: "Success",
        message: "Sent cart data successfully!",
      }));

    };

    sendcartData().catch(error)(()=>{
      dispatch(cartToggleActions.setNotification({
        status: "error", 
        title: "Error",
        message: "Sending cartdata failed!",
      }));
    });

  },[cart,dispatch]);

  return (
    <Layout>
      {isCartShow && <Cart />}
      <Products />
    </Layout>
  );
}

export default App;
